class Desaparecido_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get desaparecido by idDes
     */
    function get_desaparecido($idDes)
    {
        return $this->db->get_where('desaparecidos',array('idDes'=>$idDes))->row_array();
    }
        
    /*
     * Get all desaparecidos
     */
    function get_all_desaparecidos()
    {
        $this->db->order_by('idDes', 'desc');
