    }
    
    /*
     * function to update desaparecido
     */
    function update_desaparecido($idDes,$params)
    {
        $this->db->where('idDes',$idDes);
        return $this->db->update('desaparecidos',$params);
    }
    
    /*
     * function to delete desaparecido
     */
    function delete_desaparecido($idDes)
    {
        return $this->db->delete('desaparecidos',array('idDes'=>$idDes));
    }
